import React from "react";
import Die from "./components/die";
import { nanoid } from "nanoid";

export default function App(){
    const [dice, setDice] = React.useState(allNewDice())

    function allNewDice(){
        const newArray = []
        for (let i = 0; i <10; i++) {
            newArray.push({
                value: Math.floor(Math.random() * 7), 
                isHeld: false,
                id: nanoid()
            })
           }
       return (newArray)     
    } 


    const [dieValue, setDieValue ] = React.useState(false)

    function holdDice(id){
        setDieValue(prevDieValue => !dieValue)
        alert(id)
    }





    function rollDice(){
        setDice(allNewDice())
    }

const diceElements = dice.map( die => 

<Die 
key={die.id} 
value={die.value}
isHeld = {die.isHeld}
holdDice ={() => holdDice()}
/>
)






    return(
        <> 
        <main id="appview">
<div id="textarea">
            <h1>Tenzies</h1>
            <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
           </div>


        <div className="dice">
        {diceElements}

        </div>  


        <div className="rollbtn-div">
        <button id="rollbtn" onClick={rollDice}> Roll</button>

        </div>


         </main>
        </>
    )
}
