import React from "react";
import Die from "./components/die";

export default function App(){

    function allNewDice(){
        const newArray = {value: "", isHeld: false}


       for(var i=0; i<=9; i++){
           newArray.push({value: Math.floor(Math.random() * 7),
           isHeld: false})
           }
       return (newArray)
    } 

const [dice, setDice] = React.useState(allNewDice())
const diceElements = dice.map( die => <Die key="" value={die}/>)

function rollDice(){
    setDice(allNewDice())
}




    return(
        <> 
        <main id="appview">
<div id="textarea">
            <h1>Tenzies</h1>
            <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
           </div>


        <div className="dice">
        {diceElements}
        </div>


        <div className="rollbtn-div">
        <button id="rollbtn" onClick={rollDice}> Roll</button>

        </div>
         </main>
        </>
    )
}
