import React from "react";

export default function Sidebar(){
const [notes,setNotes] = React.useState([])
var noteid= 0;
    function newNote(){
            setNotes(notes => notes.map(item => {
                return (
                    <div>Notes {noteid+1}</div>
                )
            }))
    }

    return(
    <>
  <section id="sidebar">
     <div id="header-sidebar"> 
     <h1>Notes</h1> 
     <button id="addnote">+</button>
     {newNote}
     </div>

  </section>
    </>
    )
}
